#include<bits/stdc++.h>
using namespace std;
int main(){
   int n;
   cin >> n;
   int arr[n];
   for(int i=0;i<n;i++){
       cin >> arr[i];   // taking input
   }

   int nse[n]; // store index of next smaller for each ith , if not there store n (which mean not valid)
   for(int i=n-1;i>=0;i--){ // TC -> O(N)
      int val = i + 1;
      while(val < n && arr[val] >= arr[i]){ // Overall this while loop will run for O(N) 
          val = nse[val];
      }
      nse[i] = val;
   }

   for(int i=0;i<n;i++){
      cout << nse[i] << " ";
   }cout << '\n';
}
